mapper.select:
SELECT E.eqpmn_code, E.code_nm, E.manage_no, E.purchs_date, E.purchs_amount, NVL(F.NAME+'('+F.pern_no+')', '-') as pern_no, E.use_yn, E.use_place, E.disuse_yn FROM (SELECT b.eqpmn_code, a.code_nm, b.manage_no, b.purchs_date, b.purchs_amount, b.use_yn, b.use_place, b.disuse_yn FROM cmmn_code_manage a, eqpmn_manage b WHERE a.code (+) = b.eqpmn_code AND a.group_code NOT IN ('HODITYPE') ) E, (SELECT C.pern_no, C.name, D.manage_no FROM hpa100t C, eqpmn_user_mapping D WHERE C.pern_no = D.pern_no (+) ) F WHERE E.manage_no = F.manage_no (+) AND E.manage_no IS NOT NULL
AND E.eqpmn_code LIKE '%' || #{eqpmncode} || '%'
AND E.purchs_date = #{purchsdate}
AND E.use_yn = #{useyn}
AND E.disuse_yn = #{disuseyn}
AND E.use_place LIKE '%' || #{useplace} || '%'
ORDER BY E.purchs_date DESC

SELECT E.eqpmn_code, E.code_nm, E.manage_no, E.purchs_date, E.purchs_amount, NVL(F.NAME+'('+F.pern_no+')', '-') as pern_no, E.use_yn, E.use_place, E.disuse_yn, E.code FROM (SELECT b.eqpmn_code, a.code_nm, b.manage_no, b.purchs_date, b.purchs_amount, b.use_yn, b.use_place, b.disuse_yn, a.code FROM cmmn_code_manage a, eqpmn_manage b WHERE a.code (+) = b.eqpmn_code AND a.group_code NOT IN ('HODITYPE') ) E, (SELECT C.pern_no, C.name, D.manage_no FROM hpa100t C, eqpmn_user_mapping D WHERE C.pern_no = D.pern_no (+) ) F WHERE E.manage_no = F.manage_no (+) AND E.manage_no IS NOT NULL
AND E.code LIKE '%' || #{eqpmncode} || '%'
AND E.use_yn = #{useyn}

mapper.update:
MERGE INTO eqpmn_use_manage A USING DUAL ON A.pern_no = #{pernno}
WHEN MATCHED THEN UPDATE SET A.input_date = #{inputdate}, A.exp_date = #{expdate}, A.use_yn = #{useyn}, A.pc_manage_no1 = #{pcmanageno1}, A.pc_manage_no2 = #{pcmanageno2}, A.mo_manage_no1 = #{momanageno1}, A.mo_manage_no2 = #{momanageno2}, A.ph_manage_no = #{phmanageno}
WHEN NOT MATCHED THEN INSERT ( input_date, exp_date, use_yn, pern_no, pc_manage_no1, pc_manage_no2, mo_manage_no1, mo_manage_no2, ph_manage_no
) VALUES ( #{inputdate}, #{expdate}, #{useyn}, #{pernno}, #{pcmanageno1}, #{pcmanageno2}, #{momanageno1}, #{momanageno2}, #{phmanageno} )

MERGE INTO eqpmn_user_mapping A USING DUAL ON A.pern_no = #{pernno}
WHEN MATCHED THEN UPDATE SET A.cmos_pw = 'NULL', A.sso_id = 'NULL', A.sso_pw = 'NULL' WHERE A.manage_no = #{manageno}
WHEN NOT MATCHED THEN INSERT ( manage_no, pern_no, cmos_pw, sso_id, sso_pw ) VALUES ( #{manageno}, #{pernno}, 'NULL', 'NULL', 'NULL' )

UPDATE			eqpmn_manage A
SET				A.use_yn					= 'Y'
WHERE			A.manage_no					= #{pcmanageno1}
OR				A.manage_no					= #{pcmanageno2}
OR				A.manage_no					= #{momanageno1}
OR				A.manage_no					= #{momanageno2}
OR				A.manage_no					= #{phmanageno}

UPDATE	eqpmn_manage
SET		use_yn			= 'N'
WHERE	manage_no 		= #{pcmanageno1}
OR		manage_no		= #{pcmanageno2}
OR		manage_no		= #{momanageno1}
OR		manage_no		= #{momanageno2}
OR		manage_no		= #{phmanageno}
